import sys
import UnityEditor

class Logger(object):
    def __init__(self):
        self.terminal = sys.stdout
        self.log = UnityEditor.Scripting.Python.PythonConsoleWindow.AddToOutput

    def write(self, message):
        try:
            self.terminal.write(message)
        except IOError:
            pass

        self.log(message)  

    def flush(self):
        try:
            self.terminal.flush()
        except IOError:
            pass

sys.stdout = Logger()
